import React, {Component} from 'react';
import {
  Container,
  Header,
  Content,
  ListItem,
  CheckBox,
  Text,
  Body,
  Card,
  CardItem,
} from 'native-base';
import {Dimensions, View, StyleSheet} from 'react-native';
import {MyImage} from '../MyImage';
let {width} = Dimensions.get('window');

export default class SimpleChoiceQuestion extends Component {
  constructor(props) {
    super(props);
    this.state = {
      question: {
        ID: 1,
        stem: '单选题 1: 下面这张图片来自于',
        image:
          'data:image/jpg;base64,',
        options: [
          {
            option: 'A',
            content: '选项A',
            image:
              'data:image/jpg;base64,',
          },
          {option: 'B', content: '选项B', image: null},
          {option: 'C', content: '选项C', image: null},
          {option: 'D', content: '选项D', image: null},
        ],
      },
      answer: -1,
      stuAnswer: null,
    };
  }

  componentDidMount() {
    console.log('simple', this.props);
  }

  check = (option) => {
    if (this.state.stuAnswer === null || this.state.stuAnswer === undefined) {
      return false;
    }
    return this.state.stuAnswer.option === option;
  };

  clickBox = (props) => {
    var p = [false, false, false, false];
    p[props] = true;
    this.setState({pick: p, answer: props});
  };

  renderChoices = () => {
    return this.props.question.options.map((item) => {
      return (
        <ListItem style={{flexDirection: 'row'}}>
          <CheckBox
            checked={this.check(item.option)}
            onPress={() => {
              this.setState({stuAnswer: item});
              this.props.setSimpleChoiceAnswer(item);
            }}
          />
          <Body style={{marginLeft: 10}}>
            <Text>{item.content}</Text>
            <MyImage source={item.image} width={width * 0.7} height={200} />
          </Body>
        </ListItem>
      );
    });
  };
  render() {
    return (
      <Card style={styles.card}>
        <CardItem header bordered>
          <Text style={{fontSize: 20, color: 'black'}}>
            {this.props.question.stem}
          </Text>
        </CardItem>
        <CardItem>
          <MyImage
            source={this.props.question.image}
            width={width * 0.8}
            height={200}
          />
        </CardItem>
        {this.renderChoices()}
      </Card>
    );
  }
}

const styles = StyleSheet.create({
  card: {
    width: width,
  },
});
